(()=>{"use strict";(e=>{console.log(e);const t=document.getElementById("timer-hours"),n=document.getElementById("timer-minutes"),o=document.getElementById("timer-seconds");let m=function(e){return e>=0&&e<10?"0"+e:e},r=setInterval((()=>{let s=(()=>{let t=(new Date(e).getTime()-(new Date).getTime())/1e3;return{timeRemaining:t,hours:Math.floor(t/60/60),minutes:Math.floor(t/60%60),seconds:Math.floor(t%60)}})();t.textContent=m(s.hours),n.textContent=m(s.minutes),o.textContent=m(s.seconds),s.timeRemaining<=0&&(clearInterval(r),t.textContent="00",n.textContent="00",o.textContent="00")}),1e3)})("20 november 2021")})();